import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const CandidateDetails = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [agree, setAgree] = useState(false);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const storedEmail = sessionStorage.getItem("candidateEmail");
        if (!storedEmail) {
            navigate("/candidate-login");
            return;
        }
        setEmail(storedEmail);
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!firstName || !lastName || !address || !phone || !agree) {
            setError("All fields are required.");
            return;
        }

        try {
            const response = await fetch("http://141.148.219.190:8000/api/candidate/update-details", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    first_name: firstName,
                    last_name: lastName,
                    email: email,
                    address: address,
                    phone: phone,
                }),
            });

            if (response.ok) {
                sessionStorage.setItem("hasCompletedProfile", "true");  // ✅ Set profile as completed
                alert("Details submitted successfully!");
                navigate("/candidate-dashboard");
            } else {
                setError("Error submitting details.");
            }
        } catch (error) {
            setError("Something went wrong. Try again.");
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-indigo-500 to-purple-600 p-6">
            <div className="bg-white p-8 rounded-lg shadow-lg w-2/3 max-w-lg">
                <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Candidate Details</h2>
                {error && <p className="text-red-600 text-center">{error}</p>}
                <form onSubmit={handleSubmit}>
                    <input type="text" placeholder="First Name" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                    <input type="text" placeholder="Last Name" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                    <input type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} required />
                    <input type="text" placeholder="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} required />
                    <button type="submit" className="bg-blue-500 text-white w-full py-2 rounded">
                        Submit
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CandidateDetails;
