import React, { useState } from "react";

const HRUploadVideo = ({ candidateId, onClose }) => {
    const [video, setVideo] = useState(null);
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file && file.type.startsWith("video/")) {
            setVideo(file);
        } else {
            setError("Please upload a valid video file.");
        }
    };

    const handleUpload = async () => {
        if (!video) {
            setError("Please select a video.");
            return;
        }

        setLoading(true);
        setError("");

        const formData = new FormData();
        formData.append("candidate_id", candidateId);
        formData.append("video", video);

        try {
            const response = await fetch("http://141.148.219.190:8000/api/hr/upload-video", {
                method: "POST",
                body: formData,
            });

            if (!response.ok) {
                throw new Error(await response.text());
            }

            alert("Video uploaded successfully!");
            onClose();
        } catch (error) {
            setError(error.message || "Video upload failed.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="video-upload-modal">
            <h2>Upload Candidate Verification Video</h2>
            {error && <p className="error-message">{error}</p>}
            <input type="file" accept="video/*" onChange={handleFileChange} />
            <button onClick={handleUpload} disabled={loading}>
                {loading ? "Uploading..." : "Upload Video"}
            </button>
            <button onClick={onClose} className="cancel-btn">Cancel</button>
        </div>
    );
};

export default HRUploadVideo;
