import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./HRDashboard.css";

const API_BASE_URL = "http://141.148.219.190:8000";

const HRDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [meetings, setMeetings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // ‚úÖ Check Authentication Before Accessing Dashboard
  useEffect(() => {
    const hrEmail = localStorage.getItem("hr_email");
    if (!hrEmail) {
      navigate("/hr-login"); // Redirect to login if not authenticated
    }
  }, [navigate]);

  // ‚úÖ Fetch Candidates (with Match Data) & Meetings
  const fetchData = async () => {
    try {
      const [candidatesRes, meetingsRes] = await Promise.all([
        fetch(`${API_BASE_URL}/api/hr/candidates/match-status`),
        fetch(`${API_BASE_URL}/api/hr/meetings`),
      ]);

      if (!candidatesRes.ok || !meetingsRes.ok) {
        throw new Error(`Server Error: ${candidatesRes.statusText} & ${meetingsRes.statusText}`);
      }

      const candidatesData = await candidatesRes.json();
      const meetingsData = await meetingsRes.json();

      // ‚úÖ Get Current Date & Time
      const now = new Date();

      // ‚úÖ Filter Only Future Meetings
      const upcomingMeetings = (meetingsData.data || [])
        .filter((meeting) => new Date(meeting.scheduled_at) >= now)
        .map((meeting) => ({
          ...meeting,
          candidate_name: candidatesData.data.find((c) => c.id === meeting.candidate_id)?.first_name || "Unknown Candidate",
        }));

      setCandidates(candidatesData.data || []);
      setMeetings(upcomingMeetings);
    } catch (error) {
      console.error("‚ùå Error fetching data:", error);
      setError("Failed to fetch data. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // ‚úÖ Format Date for Meetings
  const formatMeetingTime = (dateString) => {
    if (!dateString) return "Unknown Time";

    try {
      const parsedDate = new Date(dateString);
      if (isNaN(parsedDate)) return "Invalid Date";

      return new Intl.DateTimeFormat("en-US", {
        dateStyle: "medium",
        timeStyle: "short",
      }).format(parsedDate);
    } catch (error) {
      console.error("‚ùå Date Formatting Error:", error);
      return "Invalid Date";
    }
  };

  // ‚úÖ Loading State
  if (loading) return <p>Loading HR Dashboard...</p>;

  // ‚úÖ Error State
  if (error) return <p className="error-message">{error}</p>;

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h3>üìÖ Upcoming Meetings</h3>
        {meetings.length > 0 ? (
          <ul>
            {meetings.map((meeting, index) => (
              <li key={index} className="meeting-item">
                <p>
                  <strong>{meeting.candidate_name || "Unknown Candidate"}</strong>
                </p>
                <p>üïí {formatMeetingTime(meeting.scheduled_at)}</p>

                {/* ‚úÖ Join Meeting */}
                <a
                  href={meeting.join_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="join-meeting"
                  style={{ color: "#007bff", textDecoration: "underline", cursor: "pointer" }}
                >
                  üîó Join Meeting
                </a>
              </li>
            ))}
          </ul>
        ) : (
          <p>No upcoming meetings.</p>
        )}
      </aside>

      <div className="content">
        <h2>HR Dashboard</h2>
        {candidates.length > 0 ? (
          <table className="candidate-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Candidate Name</th>
                <th>Email</th>
                <th>Verified</th>
                <th>Face Matched</th>
                <th>Confidence Score</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {candidates.map((candidate) => (
                <tr key={candidate.id}>
                  <td>{candidate.id}</td>
                  <td>
                    <button
                      className="candidate-link"
                      onClick={() => window.open(`/hr/candidate/${candidate.id}`, "_blank")}
                    >
                      {candidate.first_name} {candidate.last_name}
                    </button>
                  </td>
                  <td>{candidate.email}</td>

                  {/* ‚úÖ HR Verification Status */}
                  <td className={candidate.verified ? "verified" : "unverified"}>
                    {candidate.verified ? "‚úÖ Verified" : "‚ùå Not Verified"}
                  </td>

                  {/* ‚úÖ Face Match Status */}
                  <td className={candidate.match_found ? "matched" : "not-matched"}>
                    {candidate.match_found ? "‚úÖ Matched" : "‚ùå Not Matched"}
                  </td>

                  {/* ‚úÖ Confidence Score */}
                  <td>
                    {candidate.confidence_score !== undefined &&
                    candidate.confidence_score !== null
                      ? candidate.confidence_score.toFixed(2)
                      : "0.00"}
                  </td>

                  {/* ‚úÖ Action Buttons */}
                  <td>
                    {candidate.photo ? (
                      <a href={candidate.photo} target="_blank" rel="noopener noreferrer">
                        üì∑ View Photo
                      </a>
                    ) : (
                      <span className="disabled">üì∑ No Photo</span>
                    )}

                    {candidate.resume ? (
                      <a href={candidate.resume} target="_blank" rel="noopener noreferrer">
                        üìÑ View Resume
                      </a>
                    ) : (
                      <span className="disabled">üìÑ No Resume</span>
                    )}

                    {candidate.id_proof ? (
                      <a href={candidate.id_proof} target="_blank" rel="noopener noreferrer">
                        üÜî View ID Proof
                      </a>
                    ) : (
                      <span className="disabled">üÜî No ID Proof</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No candidates available.</p>
        )}
      </div>
    </div>
  );
};

export default HRDashboard;
