// CandidateDetails.js (React)
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const CandidateDetails = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [idProof, setIdProof] = useState(null);
    const [photo, setPhoto] = useState(null);
    const [cv, setCV] = useState(null);
    const [agree, setAgree] = useState(false);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const storedEmail = sessionStorage.getItem("candidateEmail");
        if (!storedEmail) {
            navigate("/candidate-login");
            return;
        }
        setEmail(storedEmail);
        fetchCandidateDetails(storedEmail);
    }, [navigate]);

    const fixURL = (filePath) => {
        return filePath ? filePath.replace("http://127.0.0.1:8000", "http://141.148.219.190:8000") : null;
    };

    const fetchCandidateDetails = async (email) => {
        try {
            const encodedEmail = encodeURIComponent(email);
            const apiUrl = `http://141.148.219.190:8000/api/candidate/${encodedEmail}`;
            console.log("Fetching data from:", apiUrl);
            console.log("Email value:", email); // Log the email value.

            const response = await fetch(apiUrl, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                }
            });

            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${await response.text()}`);
            }

            const data = await response.json();

            setFirstName(data.data.first_name || "");
            setLastName(data.data.last_name || "");
            setAddress(data.data.address || "");
            setPhone(data.data.phone || "");
            setPhoto(fixURL(data.data.photo));
            setIdProof(fixURL(data.data.id_proof));
            setCV(fixURL(data.data.resume));

        } catch (error) {
            console.error("Error fetching candidate details:", error);
            setError("An error occurred while fetching details. Please try again.");
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!firstName || !lastName || !address || !phone || !agree) {
            setError("All fields are required except file uploads.");
            return;
        }

        setError("");
        const formData = new FormData();
        formData.append("first_name", firstName);
        formData.append("last_name", lastName);
        formData.append("email", email);
        formData.append("address", address);
        formData.append("phone", phone);

        if (idProof && typeof idProof !== "string") formData.append("id_proof", idProof);
        if (photo && typeof photo !== "string") formData.append("photo", photo);
        if (cv && typeof cv !== "string") formData.append("cv", cv);

        try {
            const response = await fetch("http://141.148.219.190:8000/api/candidate/update-details", {
                method: "POST",
                body: formData,
            });

            if (!response.ok) {
                const errorText = await response.text();
                console.error("Error submitting details:", response.status, errorText);
                setError(`Error submitting details: ${response.status} - ${errorText || "Unknown error"}`);
                return;
            }

            alert("Details submitted successfully!");
            sessionStorage.setItem("hasCompletedProfile", "true");
            navigate("/candidate-dashboard");
        } catch (error) {
            console.error("Error submitting details:", error);
            setError("Something went wrong. Try again.");
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-indigo-500 to-purple-600 p-6">
            <div className="bg-white p-8 rounded-lg shadow-lg w-2/3 max-w-lg">
                <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Candidate Details</h2>
                {error && <p className="text-red-600 text-center">{error}</p>}

                <form onSubmit={handleSubmit}>
                    {/* ... (rest of your form) */}
                </form>
            </div>
        </div>
    );
};

export default CandidateDetails;
