import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import HRScheduleMeeting from "./HRScheduleMeeting";
import HRUploadVideo from "./HRUploadVideo"; // ✅ Import Video Upload Component
import "./HRCandidateDetails.css";

const API_BASE_URL = "http://141.148.219.190:8000";
const BASE_OCI_URL = "https://objectstorage.ap-mumbai-1.oraclecloud.com"; // ✅ OCI Object Storage Base URL

const HRCandidateDetails = () => {
  const { candidateId } = useParams();
  const [candidate, setCandidate] = useState(null);
  const [meetings, setMeetings] = useState([]);
  const [showSchedule, setShowSchedule] = useState(false);
  const [showUploadVideo, setShowUploadVideo] = useState(false); // ✅ State to show upload modal
  const [resumeText, setResumeText] = useState("");
  const [resumeFile, setResumeFile] = useState("");

  useEffect(() => {
    const fetchCandidate = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/hr/candidate/${candidateId}`);
        if (!response.ok) throw new Error("Candidate not found");
        const data = await response.json();
        setCandidate(data.data);

        if (data.data.resume) {
          fetchResume(data.data.resume);
        }
      } catch (error) {
        console.error("❌ Error fetching candidate details:", error);
      }
    };

    const fetchMeetings = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/hr/meetings/${candidateId}`);
        if (!response.ok) throw new Error("Meetings not found");
        const data = await response.json();
        setMeetings(data.data || []);
      } catch (error) {
        console.error("❌ Error fetching meetings:", error);
      }
    };

    const fetchResume = async (resumeUrl) => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/hr/candidate/${candidateId}/resume`);
        const data = await response.json();
        setResumeText(data.parsed_text || "No Resume Available");
        setResumeFile(`${BASE_OCI_URL}${data.file_url}`);
      } catch (error) {
        console.error("❌ Error parsing resume:", error);
        setResumeText("Unable to parse resume.");
      }
    };

    fetchCandidate();
    fetchMeetings();
  }, [candidateId]);

  // ✅ Function to Fix OCI URLs
  const fixURL = (filePath) => {
    if (!filePath) return null;
    if (filePath.startsWith("http")) return filePath; // Already correct
    return `${BASE_OCI_URL}${filePath}`; // ✅ Ensure it returns OCI Pre-Authenticated Request URL
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar for Meetings */}
      <aside className="sidebar">
        <h3>📅 Candidate's Meetings</h3>
        {meetings.length > 0 ? (
          <ul>
            {meetings.map((meeting, index) => (
              <li key={index} className="meeting-item">
                <p><strong>{candidate?.first_name} {candidate?.last_name}</strong></p>
                <p>🕒 {new Date(meeting.scheduled_at).toLocaleString()}</p>
                <a href={meeting.join_url} target="_blank" rel="noopener noreferrer" className="join-meeting">
                  🔗 Join Meeting
                </a>
              </li>
            ))}
          </ul>
        ) : (
          <p>No upcoming meetings.</p>
        )}
      </aside>

      {/* Candidate Details */}
      <div className="content">
        <h2>Candidate Details</h2>
        {candidate ? (
          <div className="candidate-details">
            {/* Profile Section */}
            <div className="profile-section">
              {candidate.photo ? (
                <img src={fixURL(candidate.photo)} alt="Candidate" className="candidate-photo" />
              ) : (
                <p className="text-gray-600">No photo available.</p>
              )}
              <h3>{candidate.first_name} {candidate.last_name}</h3>
              <p>Email: <a href={`mailto:${candidate.email}`}>{candidate.email}</a></p>
              <p>Verified: {candidate.verified ? "✅ Yes" : "❌ No"}</p>
            </div>

            {/* Documents Section */}
            <div className="documents-section">
              <h3>📁 Documents</h3>
              {candidate.id_proof ? (
                <a href={fixURL(candidate.id_proof)} target="_blank" rel="noopener noreferrer">
                  🆔 View ID Proof
                </a>
              ) : (
                <p className="text-gray-600">No ID Proof uploaded.</p>
              )}
              {resumeFile ? (
                <p><a href={resumeFile} download target="_blank">📄 Download Resume</a></p>
              ) : (
                <p className="text-gray-600">No resume available.</p>
              )}
            </div>

            {/* Resume Parsed Content */}
            <div className="resume-section">
              <h3>📜 Resume Content</h3>
              <pre className="resume-content">{resumeText}</pre>
            </div>

            {/* ✅ Upload Video Button */}
            <button className="upload-video-btn" onClick={() => setShowUploadVideo(true)}>
              📹 Upload Verification Video
            </button>

            {/* ✅ Show Upload Video Component */}
            {showUploadVideo && (
              <HRUploadVideo candidateId={candidateId} onClose={() => setShowUploadVideo(false)} />
            )}

            {/* Schedule Meeting */}
            <button className="schedule-meeting-btn" onClick={() => setShowSchedule(true)}>
              📅 Schedule Meeting
            </button>
            {showSchedule && <HRScheduleMeeting candidate={candidate} onClose={() => setShowSchedule(false)} />}
          </div>
        ) : (
          <p>Loading candidate details...</p>
        )}
      </div>
    </div>
  );
};

export default HRCandidateDetails;
