import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const CandidateDetails = ({ onDetailsUpdated }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [idProof, setIdProof] = useState(null);
    const [photo, setPhoto] = useState(null);
    const [cv, setCV] = useState(null);
    const [agree, setAgree] = useState(false);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const storedEmail = sessionStorage.getItem("candidateEmail");
        if (!storedEmail) {
            navigate("/candidate-login");
            return;
        }
        setEmail(storedEmail);
        fetchCandidateDetails(storedEmail);
    }, [navigate]);

    const fetchCandidateDetails = async (email) => {
        try {
            const apiUrl = `http://127.0.0.1:8000/api/candidate/${encodeURIComponent(email)}`;
            console.log("Fetching data from:", apiUrl);

            const response = await fetch(apiUrl);

            // ✅ Log raw response
            const text = await response.text();
            console.log("Raw API Response:", text);

            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${text}`);
            }

            // ✅ Ensure valid JSON
            const data = JSON.parse(text);
            setFirstName(data.data.first_name || "");
            setLastName(data.data.last_name || "");
            setAddress(data.data.address || "");
            setPhone(data.data.phone || "");

            // ✅ Fix incorrect file path issue
            setPhoto(data.data.photo ? data.data.photo.replace("/uploads/uploads/", "/uploads/") : null);
            setIdProof(data.data.id_proof ? data.data.id_proof.replace("/uploads/uploads/", "/uploads/") : null);
            setCV(data.data.resume ? `/api/uploads/resume/${data.data.resume}` : null);
        } catch (error) {
            console.error("Error fetching candidate details:", error);
            setError("An error occurred while fetching details. Please try again.");
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!firstName || !lastName || !address || !phone || !agree) {
            setError("All fields are required except file uploads.");
            return;
        }

        setError("");
        const formData = new FormData();
        formData.append("first_name", firstName);
        formData.append("last_name", lastName);
        formData.append("email", email);
        formData.append("address", address);
        formData.append("phone", phone);

        if (idProof && typeof idProof !== "string") formData.append("id_proof", idProof);
        if (photo && typeof photo !== "string") formData.append("photo", photo);
        if (cv && typeof cv !== "string") formData.append("cv", cv);

        try {
            const response = await fetch("http://127.0.0.1:8000/api/candidate/update-details", {
                method: "POST",
                body: formData,
            });

            if (!response.ok) {
                const errorText = await response.text();
                console.error("Error submitting details:", response.status, errorText);
                setError(`Error submitting details: ${response.status} - ${errorText || "Unknown error"}`);
                return;
            }

            const result = await response.json();
            console.log("Success Result:", result);

            alert("Details submitted successfully!");

            // ✅ Redirect to Candidate Dashboard after success
            navigate("/candidate-dashboard");

        } catch (error) {
            console.error("Error submitting details:", error);
            setError("Something went wrong. Try again.");
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-indigo-500 to-purple-600 p-6">
            <div className="bg-white p-8 rounded-lg shadow-lg w-2/3 max-w-lg">
                <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Candidate Details</h2>
                {error && <p className="text-red-600 text-center">{error}</p>}

                <form onSubmit={handleSubmit}>
                    <div className="grid grid-cols-2 gap-4">
                        <input type="text" placeholder="First Name" className="border p-2 rounded" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                        <input type="text" placeholder="Last Name" className="border p-2 rounded" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                    </div>

                    <input type="text" placeholder="Address" className="border p-2 w-full rounded mt-4" value={address} onChange={(e) => setAddress(e.target.value)} required />
                    <input type="text" placeholder="Phone" className="border p-2 w-full rounded mt-4" value={phone} onChange={(e) => setPhone(e.target.value)} required />

                    <div className="mt-4">
                        <label className="block font-semibold">ID Proof (JPEG/PNG, max 1.5MB):</label>
                        <input type="file" className="border p-2 w-full rounded" accept="image/jpeg, image/png" onChange={(e) => setIdProof(e.target.files[0])} />
                        {idProof && typeof idProof === "string" && (
                            <a href={idProof} target="_blank" rel="noopener noreferrer">View ID Proof</a>
                        )}
                    </div>

                    <div className="mt-4">
                        <label className="block font-semibold">Photo (JPEG/PNG, max 1.5MB):</label>
                        <input type="file" className="border p-2 w-full rounded" accept="image/jpeg, image/png" onChange={(e) => setPhoto(e.target.files[0])} />
                        {photo && typeof photo === "string" && (
                            <a href={photo} target="_blank" rel="noopener noreferrer">View Photo</a>
                        )}
                    </div>

                    <div className="mt-4">
                        <label className="block font-semibold">CV (PDF/DOC/DOCX):</label>
                        <input type="file" className="border p-2 w-full rounded" accept=".pdf,.doc,.docx" onChange={(e) => setCV(e.target.files[0])} />
                        {cv && typeof cv === "string" && (
                            <a href={cv} target="_blank" rel="noopener noreferrer">View Resume</a>
                        )}
                    </div>

                    <div className="mt-4 flex items-center">
                        <input type="checkbox" className="mr-2" checked={agree} onChange={() => setAgree(!agree)} required />
                        <label>I acknowledge that all the information shared by me is true.</label>
                    </div>

                    <button type="submit" className="mt-6 bg-blue-500 text-white w-full py-2 rounded font-semibold hover:bg-blue-600 transition-all">
                        Submit
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CandidateDetails;
