import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./HRDashboard.css";

const HRDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [meetings, setMeetings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // ✅ Check Authentication Before Accessing Dashboard
  useEffect(() => {
    const hrEmail = localStorage.getItem("hr_email");
    if (!hrEmail) {
      navigate("/hr-login"); // Redirect to login if not authenticated
    }
  }, [navigate]);

  // ✅ Function to Fetch Candidates & Meetings
  const fetchData = async () => {
    try {
      const [candidatesRes, meetingsRes] = await Promise.all([
        fetch("http://141.148.219.190:8000/api/hr/candidates"),
        fetch("http://141.148.219.190:8000/api/hr/meetings"),
      ]);

      if (!candidatesRes.ok || !meetingsRes.ok) {
        throw new Error(`Server Error: ${candidatesRes.statusText} & ${meetingsRes.statusText}`);
      }

      const candidatesData = await candidatesRes.json();
      const meetingsData = await meetingsRes.json();

      const candidatesMap = {};
      (candidatesData.data || []).forEach(candidate => {
        candidatesMap[candidate.id] = `${candidate.first_name} ${candidate.last_name}`;
      });

      // ✅ Get Current Date & Time
      const now = new Date();

      // ✅ Filter Only Future Meetings
      const upcomingMeetings = (meetingsData.data || []).filter(meeting => {
        const meetingDate = new Date(meeting.scheduled_at);
        return meetingDate >= now; // ✅ Show only future meetings
      }).map(meeting => ({
        ...meeting,
        candidate_name: candidatesMap[meeting.candidate_id] || "Unknown Candidate",
      }));

      setCandidates(candidatesData.data || []);
      setMeetings(upcomingMeetings);
    } catch (error) {
      console.error("❌ Error fetching data:", error);
      setError("Failed to fetch data. Please try again later.");
    } finally {
      setLoading(false);
    }
  };



  useEffect(() => {
    fetchData();
  }, []);

  // ✅ Helper Function to Format Date Safely
  const formatMeetingTime = (dateString) => {
    if (!dateString) return "Unknown Time";

    try {
      const parsedDate = new Date(dateString);
      if (isNaN(parsedDate)) return "Invalid Date";

      return new Intl.DateTimeFormat("en-US", {
        dateStyle: "medium",
        timeStyle: "short",
      }).format(parsedDate);
    } catch (error) {
      console.error("❌ Date Formatting Error:", error);
      return "Invalid Date";
    }
  };

  // ✅ Loading State
  if (loading) return <p>Loading HR Dashboard...</p>;

  // ✅ Error State
  if (error) return <p className="error-message">{error}</p>;

  return (
    <div className="dashboard-container">
  <aside className="sidebar">
    <h3>📅 Upcoming Meetings</h3>
    {meetings.length > 0 ? (
      <ul>
        {meetings.map((meeting, index) => (
          <li key={index} className="meeting-item">
            <p><strong>{meeting.candidate_name || "Unknown Candidate"}</strong></p>
            <p>🕒 {formatMeetingTime(meeting.scheduled_at)}</p>

            {/* ✅ Ensure join_url from database is used */}
            <a
              href={meeting.join_url}
              target="_blank"
              rel="noopener noreferrer"
              className="join-meeting"
              style={{ color: "#007bff", textDecoration: "underline", cursor: "pointer" }}
            >
              🔗 Join Meeting
            </a>
          </li>
        ))}
      </ul>
    ) : (
      <p>No upcoming meetings.</p>
    )}
</aside>




      <div className="content">
        <h2>HR Dashboard</h2>
        {candidates.length > 0 ? (
          <table className="candidate-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Candidate Name</th>
                <th>Email</th>
                <th>Verified</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {candidates.map((candidate) => (
                <tr key={candidate.id}>
                  <td>{candidate.id}</td>
                  <td>
                    <button
                      className="candidate-link"
                      onClick={() => window.open(`/hr/candidate/${candidate.id}`, "_blank")}
                    >
                      {candidate.first_name} {candidate.last_name}
                    </button>
                  </td>
                  <td>{candidate.email}</td>
                  <td className={candidate.verified ? "verified" : "unverified"}>
                    {candidate.verified ? "✅ Verified" : "❌ Not Verified"}
                  </td>
                  <td>
                    {candidate.photo && (
                      <a href={`http://141.148.219.190:8000/uploads/photo/${candidate.email}_photo.jpg`} target="_blank">
                        📷 View Photo
                      </a>
                    )}
                    {candidate.resume && (
                      <a href={`http://141.148.219.190:8000/uploads/resume/${candidate.email}_resume.pdf`} target="_blank">
                        📄 View Resume
                      </a>
                    )}
                    {candidate.id_proof && (
                      <a href={`http://141.148.219.190:8000/uploads/id_proof/${candidate.email}_id.jpg`} target="_blank">
                        🆔 View ID Proof
                      </a>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No candidates available.</p>
        )}
      </div>
    </div>
  );
};

export default HRDashboard;
