import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CandidateDetails from "./CandidateDetails";

const CandidateDashboard = () => {
    const [candidate, setCandidate] = useState(null);
    const [interviews, setInterviews] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const navigate = useNavigate();
    const email = sessionStorage.getItem("candidateEmail");

    useEffect(() => {
        if (!email) {
            navigate("/candidate-login");
            return;
        }

        const fetchData = async () => {
          try {
            const apiUrl = `http://141.148.219.190:8000/api/candidate/${email}?nocache=${Date.now()}`;
              console.log("Fetching data from:", apiUrl);

              const response = await fetch(apiUrl);

              // ✅ Check response status
              if (!response.ok) {
                  console.error("Error fetching candidate data:", response.status, await response.text());
                  throw new Error(`HTTP ${response.status} - API request failed`);
              }

              // ✅ Ensure JSON parsing
              const data = await response.json();
              setCandidate(data.data);
          } catch (error) {
              console.error("Error fetching data:", error);
          }
      };      

        fetchData();
    }, [email, navigate]);

    const handleLogout = () => {
        sessionStorage.removeItem("candidateEmail");
        sessionStorage.removeItem("candidateCompletedDetails"); // Remove any other relevant data
        navigate("/candidate-login"); // Redirect to login page
    };

    if (!candidate) {
        return <div className="text-center mt-10 text-xl font-bold">Loading Candidate Details...</div>;
    }

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleDetailsUpdated = () => {
        setIsEditing(false);
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center p-10">
            <div className="flex justify-between w-full mb-6 px-6">
                <h1 className="text-3xl font-bold">Candidate Dashboard</h1>
                <button 
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                    onClick={handleLogout}
                >
                    Sign Out
                </button>
            </div>

            <div className="bg-white p-6 shadow-md rounded-lg w-3/4">
                <h2 className="text-2xl font-semibold mb-4">Profile Information</h2>
                <p><strong>Name:</strong> {candidate.first_name} {candidate.last_name}</p>
                <p><strong>Email:</strong> {candidate.email}</p>
                <p><strong>Phone:</strong> {candidate.phone}</p>
                <p><strong>Address:</strong> {candidate.address}</p>

                {candidate.id_proof && (
                    <p>
                        <strong>ID Proof:</strong>{" "}
                        <a href={`/api/uploads/id_proof/${candidate.id_proof}`} target="_blank" rel="noopener noreferrer">
                            View
                        </a>
                    </p>
                )}

                {candidate.resume && (
                    <p>
                        <strong>Resume:</strong>{" "}
                        <a href={`/api/uploads/resume/${candidate.resume}`} target="_blank" rel="noopener noreferrer">
                            View
                        </a>
                    </p>
                )}

                {candidate.photo && (
                    <p>
                        <strong>Photo:</strong>{" "}
                        <img
                            src={`/api/uploads/photo/${candidate.photo}`}
                            alt="Candidate Photo"
                            className="max-w-full h-auto"
                        />
                    </p>
                )}

                <button
                    className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
                    onClick={handleEditClick}
                >
                    Edit Details
                </button>
            </div>

            <div className="bg-white p-6 shadow-md rounded-lg w-3/4 mt-6">
                <h2 className="text-2xl font-semibold mb-4">Upcoming Interviews</h2>
                {interviews.length > 0 ? (
                    <ul>
                        {interviews.map((interview, index) => (
                            <li key={index} className="border p-3 mb-2">
                                <strong>Company:</strong> {interview.company} <br />
                                <strong>Date:</strong> {interview.date} <br />
                                <strong>Time:</strong> {interview.time} <br />
                                {interview.meeting_link && (
                                    <a
                                        href={interview.meeting_link}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="text-blue-600"
                                    >
                                        Join Zoom Meeting
                                    </a>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-gray-600">No upcoming interviews.</p>
                )}
            </div>

            {isEditing && (
                <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div className="bg-white p-8 rounded-lg shadow-lg w-2/3 max-w-lg">
                        <CandidateDetails onDetailsUpdated={handleDetailsUpdated} />
                        <button onClick={() => setIsEditing(false)} className="mt-4 bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded">
                            Close Edit
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CandidateDashboard;
