import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const CandidateDetails = ({ onClose, onDetailsUpdated }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [idProof, setIdProof] = useState(null);
    const [photo, setPhoto] = useState(null);
    const [cv, setCV] = useState(null);
    const [agree, setAgree] = useState(false);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const storedEmail = sessionStorage.getItem("candidateEmail");
        if (!storedEmail) {
            navigate("/candidate-login");
            return;
        }
        setEmail(storedEmail);
        fetchCandidateDetails(storedEmail);
    }, [navigate]);

    const fetchCandidateDetails = async (email) => {
        try {
            const encodedEmail = encodeURIComponent(email);
            const apiUrl = `http://141.148.219.190:8000/api/candidate/${encodedEmail}`;
            console.log("Fetching data from:", apiUrl);

            const response = await fetch(apiUrl);
            if (!response.ok) throw new Error(`HTTP ${response.status}: ${await response.text()}`);

            const data = await response.json();
            console.log("Fetched Candidate Data:", data);

            setFirstName(data.data.first_name || "");
            setLastName(data.data.last_name || "");
            setAddress(data.data.address || "");
            setPhone(data.data.phone || "");

            // ✅ Use correct Pre-Authenticated Request (PAR) URLs
            setPhoto(data.data.photo);
            setIdProof(data.data.id_proof);
            setCV(data.data.resume);
        } catch (error) {
            console.error("Error fetching candidate details:", error);
            setError("An error occurred while fetching details. Please try again.");
        }
    };

    return (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-3/4 max-w-3xl relative max-h-[90vh] overflow-y-auto">
                {/* Close Button */}
                <button 
                    onClick={onClose} 
                    className="absolute top-4 right-4 text-gray-600 hover:text-red-600 text-lg font-bold"
                >
                    ✕
                </button>

                <h2 className="text-2xl font-bold text-center text-gray-800 mb-4">Candidate Details</h2>
                {error && <p className="text-red-600 text-center">{error}</p>}

                <form className="overflow-y-auto max-h-[70vh] p-2">
                    <div className="grid grid-cols-2 gap-4">
                        <input type="text" placeholder="First Name" className="border p-2 rounded" value={firstName} readOnly />
                        <input type="text" placeholder="Last Name" className="border p-2 rounded" value={lastName} readOnly />
                    </div>

                    <input type="text" placeholder="Address" className="border p-2 w-full rounded mt-4" value={address} readOnly />
                    <input type="text" placeholder="Phone" className="border p-2 w-full rounded mt-4" value={phone} readOnly />

                    {/* File Display Fields with Correct OCI URLs */}
                    <div className="mt-4">
                        <label className="block font-semibold">ID Proof:</label>
                        {idProof && (
                            <a href={idProof} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
                                View ID Proof
                            </a>
                        )}
                    </div>

                    <div className="mt-4">
                        <label className="block font-semibold">Photo:</label>
                        {photo && (
                            <img src={photo} alt="Candidate Photo" className="max-w-full h-auto mt-2" />
                        )}
                    </div>

                    <div className="mt-4">
                        <label className="block font-semibold">CV (PDF/DOC/DOCX):</label>
                        {cv && (
                            <a href={cv} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
                                View Resume
                            </a>
                        )}
                    </div>

                    <button 
                        onClick={onClose} 
                        className="mt-6 bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded w-full"
                    >
                        Close
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CandidateDetails;
