import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const CandidateDetails = ({ onClose, onDetailsUpdated }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [idProof, setIdProof] = useState(null);
    const [photo, setPhoto] = useState(null);
    const [cv, setCV] = useState(null);
    const [agree, setAgree] = useState(false); // âœ… Added agree checkbox
    const [error, setError] = useState("");
    const navigate = useNavigate();

    const BASE_OCI_URL = "https://objectstorage.ap-mumbai-1.oraclecloud.com";

    useEffect(() => {
        const storedEmail = sessionStorage.getItem("candidateEmail");
        if (!storedEmail) {
            navigate("/candidate-login");
            return;
        }
        setEmail(storedEmail);
        fetchCandidateDetails(storedEmail);
    }, [navigate]);

    const fixURL = (filePath) => {
        if (!filePath) return null;
        if (filePath.startsWith("http")) return filePath;
        return `${BASE_OCI_URL}${filePath}`;
    };

    const fetchCandidateDetails = async (email) => {
        try {
            const encodedEmail = encodeURIComponent(email);
            const apiUrl = `http://141.148.219.190:8000/api/candidate/${encodedEmail}`;

            const response = await fetch(apiUrl, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                }
            });

            if (!response.ok) {
                throw new Error(`HTTP ${response.status}: ${await response.text()}`);
            }

            const data = await response.json();

            setFirstName(data.data.first_name || "");
            setLastName(data.data.last_name || "");
            setAddress(data.data.address || "");
            setPhone(data.data.phone || "");

            setPhoto(fixURL(data.data.photo));
            setIdProof(fixURL(data.data.id_proof));
            setCV(fixURL(data.data.resume));

        } catch (error) {
            console.error("Error fetching candidate details:", error);
            setError("An error occurred while fetching details. Please try again.");
        }
    };

    const handleFileChange = (event, setFile) => {
        const file = event.target.files[0];
        setFile(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!firstName || !lastName || !address || !phone || !agree) {
            setError("All fields are required except file uploads.");
            return;
        }

        setError("");
        const formData = new FormData();
        formData.append("email", email);
        formData.append("first_name", firstName);
        formData.append("last_name", lastName);
        formData.append("address", address);
        formData.append("phone", phone);

        if (idProof && typeof idProof !== "string") formData.append("id_proof", idProof);
        if (photo && typeof photo !== "string") formData.append("photo", photo);
        if (cv && typeof cv !== "string") formData.append("resume", cv);

        try {
            const response = await fetch("http://141.148.219.190:8000/api/candidate/update-details", {
                method: "POST",
                body: formData,
            });

            if (!response.ok) {
                const errorText = await response.text();
                setError(`Error submitting details: ${response.status} - ${errorText || "Unknown error"}`);
                return;
            }

            alert("Details submitted successfully!");
            sessionStorage.setItem("hasCompletedProfile", "true");

            if (onDetailsUpdated) {
                onDetailsUpdated();
            }

            onClose();
        } catch (error) {
            setError("Something went wrong. Try again.");
        }
    };

    return (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-3/4 max-w-3xl relative max-h-[90vh] overflow-y-auto">

                {/* âœ… Fixed Close (X) Button Issue */}
                <button 
                    onClick={(e) => {
                        e.stopPropagation(); // Prevent event bubbling issues
                        onClose();
                    }} 
                    className="absolute top-4 right-4 text-gray-600 hover:text-red-600 text-lg font-bold"
                >
                    âœ•
                </button>

                <h2 className="text-2xl font-bold text-center text-gray-800 mb-4">Candidate Details</h2>
                {error && <p className="text-red-600 text-center">{error}</p>}

                <form onSubmit={handleSubmit} className="overflow-y-auto max-h-[70vh] p-2">
                    <div className="grid grid-cols-2 gap-4">
                        <input type="text" placeholder="First Name" className="border p-2 rounded" 
                            value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                        <input type="text" placeholder="Last Name" className="border p-2 rounded" 
                            value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                    </div>

                    <input type="text" placeholder="Address" className="border p-2 w-full rounded mt-4" 
                        value={address} onChange={(e) => setAddress(e.target.value)} required />
                    <input type="text" placeholder="Phone" className="border p-2 w-full rounded mt-4" 
                        value={phone} onChange={(e) => setPhone(e.target.value)} required />

                    {/* âœ… File Upload Fields Added */}
                    <div className="mt-4">
                        <label className="block font-semibold">ID Proof:</label>
                        {idProof && (
                            <a href={idProof} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
                                View ID Proof
                            </a>
                        )}
                        <input type="file" className="border p-2 w-full rounded mt-2" 
                            accept="image/jpeg, image/png" onChange={(e) => handleFileChange(e, setIdProof)} />
                    </div>

                    <div className="mt-4">
                        <label className="block font-semibold">Photo:</label>
                        {photo && (
                            <img src={photo} alt="Candidate Photo" className="max-w-full h-auto mt-2" />
                        )}
                        <input type="file" className="border p-2 w-full rounded mt-2" 
                            accept="image/jpeg, image/png" onChange={(e) => handleFileChange(e, setPhoto)} />
                    </div>

                    <div className="mt-4">
                        <label className="block font-semibold">CV (PDF/DOC/DOCX):</label>
                        {cv && (
                            <a href={cv} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">
                                View Resume
                            </a>
                        )}
                        <input type="file" className="border p-2 w-full rounded mt-2" 
                            accept=".pdf,.doc,.docx" onChange={(e) => handleFileChange(e, setCV)} />
                    </div>

                    {/* âœ… Added Agree Checkbox */}
                    <div className="mt-4 flex items-center">
                        <input 
                            type="checkbox" 
                            id="agree" 
                            className="mr-2"
                            checked={agree} 
                            onChange={() => setAgree(!agree)}
                            required 
                        />
                        <label htmlFor="agree" className="text-gray-700">
                            I confirm that the provided details are accurate.
                        </label>
                    </div>

                    <button type="submit" className="mt-6 bg-blue-500 text-white w-full py-2 rounded font-semibold hover:bg-blue-600 transition-all">
                        Submit
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CandidateDetails;
