import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import HRScheduleMeeting from "./HRScheduleMeeting";  // ‚úÖ Import Schedule Meeting Component
import "./HRCandidateDetails.css";

const API_BASE_URL = "http://141.148.219.190:8000";

const HRCandidateDetails = () => {
  const { candidateId } = useParams();
  const [candidate, setCandidate] = useState(null);
  const [meetings, setMeetings] = useState([]);
  const [showSchedule, setShowSchedule] = useState(false);
  const [resumeText, setResumeText] = useState("");
  const [resumeFile, setResumeFile] = useState("");

  useEffect(() => {
    const fetchCandidate = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/hr/candidate/${candidateId}`);
        if (!response.ok) throw new Error("Candidate not found");
        const data = await response.json();
        setCandidate(data.data);

        // Fetch Resume Text if Available
        if (data.data.resume) {
          fetchResume(data.data.resume);
        }
      } catch (error) {
        console.error("‚ùå Error fetching candidate details:", error);
      }
    };

    const fetchMeetings = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/hr/meetings/${candidateId}`);
        if (!response.ok) throw new Error("Meetings not found");
        const data = await response.json();
        setMeetings(data.data || []);
      } catch (error) {
        console.error("‚ùå Error fetching meetings:", error);
      }
    };

    const fetchResume = async (resumeUrl) => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/hr/candidate/${candidateId}/resume`);
        const data = await response.json();
        setResumeText(data.parsed_text || "No Resume Available");
        setResumeFile(`${API_BASE_URL}${data.file_url}`);
      } catch (error) {
        console.error("‚ùå Error parsing resume:", error);
        setResumeText("Unable to parse resume.");
      }
    };

    fetchCandidate();
    fetchMeetings();
  }, [candidateId]);

  return (
    <div className="dashboard-container">
      {/* Sidebar for Meetings */}
      <aside className="sidebar">
        <h3>üìÖ Candidate's Meetings</h3>
        {meetings.length > 0 ? (
          <ul>
            {meetings.map((meeting, index) => (
              <li key={index} className="meeting-item">
                <p><strong>{candidate?.first_name} {candidate?.last_name}</strong></p>
                <p>üïí {new Date(meeting.scheduled_at).toLocaleString()}</p>
                <a href={meeting.join_url} target="_blank" rel="noopener noreferrer" className="join-meeting">
                  üîó Join Meeting
                </a>
              </li>
            ))}
          </ul>
        ) : (
          <p>No upcoming meetings.</p>
        )}
      </aside>

      {/* Candidate Details */}
      <div className="content">
        <h2>Candidate Details</h2>
        {candidate ? (
          <div className="candidate-details">
            {/* Profile Section */}
            <div className="profile-section">
              <img src={`${API_BASE_URL}${candidate.photo}`} alt="Candidate" className="candidate-photo" />
              <h3>{candidate.first_name} {candidate.last_name}</h3>
              <p>Email: <a href={`mailto:${candidate.email}`}>{candidate.email}</a></p>
              <p>Verified: {candidate.verified ? "‚úÖ Yes" : "‚ùå No"}</p>
            </div>

            {/* Documents Section */}
            <div className="documents-section">
              <h3>üìÅ Documents</h3>
              {candidate.id_proof && (
                <img src={`${API_BASE_URL}${candidate.id_proof}`} alt="ID Proof" className="id-proof-image" />
              )}
              <p><a href={resumeFile} download target="_blank">üìÑ Download Resume</a></p>
            </div>

            {/* Resume Parsed Content */}
            <div className="resume-section">
              <h3>üìú Resume Content</h3>
              <pre className="resume-content">{resumeText}</pre>
            </div>

            {/* Schedule Meeting */}
            <button className="schedule-meeting-btn" onClick={() => setShowSchedule(true)}>
              üìÖ Schedule Meeting
            </button>
            {showSchedule && <HRScheduleMeeting candidate={candidate} onClose={() => setShowSchedule(false)} />}
          </div>
        ) : (
          <p>Loading candidate details...</p>
        )}
      </div>
    </div>
  );
};

export default HRCandidateDetails;
