import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "./HRUploadVideo.css";

const HRUploadVideo = () => {
  const { candidateId } = useParams();
  const [video, setVideo] = useState(null);
  const [message, setMessage] = useState("");

  const handleUpload = async () => {
    if (!video) {
      setMessage("Please select a video.");
      return;
    }

    const formData = new FormData();
    formData.append("candidate_id", candidateId);
    formData.append("video", video);

    try {
      const response = await fetch("http://141.148.219.190:8000/api/hr/upload-video", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setMessage(data.success ? "Video uploaded successfully!" : "Failed to upload video.");
    } catch (error) {
      setMessage("Error uploading video.");
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload Video</h2>
      <input type="file" accept="video/*" onChange={(e) => setVideo(e.target.files[0])} />
      <button onClick={handleUpload}>Upload</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default HRUploadVideo;
